import React from 'react'
import { PropTypes as T } from 'prop-types'
import Map, {GoogleApiWrapper} from 'google-maps-react'
import {searchNearby, searchQuery, geoLocateUser, initAutocomplete} from '../utils/googleApiHelpers'
import './maps-edit-container.css'


//import Header from '../components/Header/Header'
import Sidebar from '../components/Sidebar/Sidebar'
import styles from './styles.module.css'

const mapStyles = {
  width: '50%',
  height: '50%'
};

export class MapContainer extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      places: [],
      pagination: null
    }
  }

  onReady(mapProps, map) {
    initAutocomplete(this.props.google, map)
    searchNearby(
      this.props.google,
      map,
      {
        location: map.center,
        radius: '1000',
        types: ['bank']
      }
    ).then((results, pagination) => {
      this.setState({
        places: results,
        pagination
      })
    }).catch((status) => {
      console.log('error fetching nearby', status)
    })
  }

  onMapMove() {}

  onMarkerClick(item) { // history stuff
    //const {push} = this.context.router;
    const {place} = item;
    //push(`/map/detail/${place.place_id}`)
  }

  render() {
    let children = null;
    if (this.props.children) {
      children = React.cloneElement(this.props.children, {
        google: this.props.google,
        places: this.state.places,
        loaded: this.props.loaded,
        router: this.context.router,
        onMove: this.onMapMove.bind(this),
        onMarkerClick: this.onMarkerClick.bind(this),
        zoom: this.props.zoom
      })
    }

    return (
      <div className="map-and-sidebar-container">
        <div className="sidebar">
          <Sidebar
            title={'SIDEBAR TITLE'}
            onListItemClick={this.onMarkerClick.bind(this)}
            places={this.state.places} />
        </div>
        <div className="map">
          <Map
            google={this.props.google}
            onReady={this.onReady.bind(this)}
            visible={true}
            className={styles.wrapper}
            style={mapStyles}
            initialCenter = {{ lat: 40.7831, lng: -73.9712 }}
            scaleControl={true}
            zoomControl={true}
            zoomControlOptions={{position: this.props.google.maps.ControlPosition.LEFT_CENTER}}
            >
            <input
              id="pac-input"
              className="controls"
              type="text"
              placeholder="Search Box"
            />
              <div className={styles.content}>
                {children}
              </div>
          </Map>
        </div>
      </div>

    )
  }
}

MapContainer.contextTypes = {
  router: T.object
}

const GAPI_KEY = process.env.REACT_APP_GAPI_KEY;
export default GoogleApiWrapper({
  apiKey: GAPI_KEY
})(MapContainer)
