import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import MapContainer from '../../views/maps-container'
import styles from './styles.module.css'

const Collection = props => (
  <tr>
    <td>{props.collection.name}</td>
    <td>{props.collection.address}</td>
    <td>{props.collection.type}</td>
    <td>{props.collection.comments}</td>
    <td>
      <Link to={"/edit/"+props.collection}>edit</Link> | <a href="#" onClick={() => { props.deletePlace(props.collection) }}>delete</a>
    </td>
  </tr>
)

export default class CollectionsList extends Component {
  constructor(props) {
    super(props);

    this.deletePlace = this.deletePlaceFromCollection.bind(this);
    this.viewCollection = this.viewCollection.bind(this);

    this.state = {collections: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        console.log(response.data.collectionsList)
        this.setState({ collections: response.data.collectionsList })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  viewCollection() {

  }

  deletePlaceFromCollection(placeName) {
    axios.delete('http://localhost:5000/collections/'+placeName)
      .then(response => { console.log(response.data)});

    this.setState({
      collections: this.state.collections.filter(el => el._placeName !== placeName)
    })
  }

  collectionsList() {
    return this.state.collections.map(currentPlace => {
      return <Collection collection={currentPlace} deletePlace={this.deletePlaceFromCollection} key={currentPlace._placeName}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>View Collections</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Collection Name</th>
              <th>Place Name</th>
              <th>Type</th>
              <th>Address</th>
              <th>Comments</th>
            </tr>
          </thead>
          <tbody className = {styles.wrapper}>
            { this.collectionsList() }
          </tbody>
        </table>
        <div>
          <MapContainer />
        </div>
      </div>
    )
  }
}
