import React from 'react'
import { getDetails } from '../../utils/googleApiHelpers'

import styles from './styles.module.css'

export class Detail extends React.Component {

  constructor(props, context) {
    super(props, context)

    this.state = {
      loading: true,
      place: {},
      location: {},
      showDetails: true
    }
  }

  componentDidMount() {
    console.log("Detail props: ", this.props)
    if (this.props.map) {
      this.getDetails(this.props.map)
    }
  }

  componentDidUpdate(prevProps) {
    if (this.props.map &&
        (prevProps.map !== this.props.map ||
         prevProps.placeId !== this.props.placeId)) {
      this.getDetails(this.props.map);
      this.setState({showDetails: !this.state.showDetails})
    }
  }


  renderPhotos(place) {
    if (!place.photos || place.photos.length === 0) return;

    const cfg = {maxWidth: 100, maxHeight: 100}
    return (<div className={styles.photoStrip}>
      {place.photos.map(p => {
        const url = `${p.getUrl(cfg)}.png`
        return (<img key={url} src={url} alt={"unable to find img"}/>)
      })}
    </div>)
  }

  getDetails(map) {
    const { google } = this.props; // this.props.google, this.props.params
    const placeId = this.props.placeId;

    this.setState({
      loading: true
    }, () => {
      getDetails(google, map, placeId)
        .then((place) => {
          const {location} = place.geometry; // place.geometry.location
          const loc = {
            lat: location.lat(),
            lng: location.lng()
          }
          this.setState({
            place: place,
            location: loc,
            loading: false
          })
        })
        .catch(err => console.log("error: ", err))
    });
  }


  render() {
    if (this.state.loading) {
      return (<div className={styles.wrapper}>
        Loading...
      </div>)
    }

    const {place} = this.state; // this.state.place
    console.log("place information: ", place)

    return (
      <div className={styles.details} showDetails={this.state.showDetails}>

        <div className={styles.closeDetail}>
          <h2 onClick={this.closeDetails}>X</h2>
          <h2>{place.name}</h2> 
        </div>

        <div className={styles.body}>
          <h2>Address: {place.formatted_address}</h2>
        </div>

        {this.renderPhotos(this.state.place)}

      </div>
    )
  }
}

export default Detail
