import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import MapContainer from '../../views/maps-container'

import { PropTypes as T } from 'prop-types'
import Map, {GoogleApiWrapper} from 'google-maps-react'
import {searchNearby, searchQuery, geoLocateUser, initAutocomplete} from '../../utils/googleApiHelpers'


//import Header from '../components/Header/Header'
import Sidebar from '../Sidebar/Sidebar'
import styles from './styles.module.css'
//import './maps-edit-container.css'

const Collection = props => (

  <tr>
    <td>{props.collectionName}</td>
    <td>{props.placeName}</td>
    <td>{props.address}</td>
    <td>{props.type}</td>
    <td>{props.comments}</td>
    <td>
      <Link to={"/edit/"+props.places}>edit</Link> | <a href="#" onClick={e => { props.deletePlaceFromCollection(props.places.name) }}>delete</a>
    </td>
  </tr>
)

const mapStyles = {
  width: '50%',
  height: '50%'
};


export default class CollectionsList extends Component {
  constructor(props) {
    super(props);

    this.deletePlace = this.deletePlaceFromCollection.bind(this);
    this.viewCollection = this.viewCollection.bind(this);

    this.state = {collections: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        this.setState({ collections: response.data.collectionsList.filter(el => el.collectionName !== 'users') })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  viewCollection() {

  }

  deletePlaceFromCollection(e) {
    /*
    axios.delete('http://localhost:5000/collections/'+placeName)
      .then(response => { console.log(response.data)});

    this.setState({
      collections: this.state.collections.filter(el => el._placeName !== placeName)
    })
    */
  }

  collectionsList() {
    return this.state.collections.map(currentCollection => {
      return <Collection 
        collection={currentCollection.name}
        placeName={""}
        address={""}
        type={""}
        comments={""}
        key={currentCollection.collectionName}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>View Collections</h3>
        <Map
          google={this.props.google}
          onReady={this.onReady.bind(this)}
          visible={false}
          className={styles.wrapper}
          style={mapStyles}
          initialCenter = {{ lat: 40.7831, lng: -73.9712 }}
          zoomControl={true}
          zoomControlOptions={{position: this.props.google.maps.ControlPosition.LEFT_CENTER}}>
          {/*
          <input
            id="pac-input"
            className="controls"
            type="text"
            placeholder="Search Box"
          />
          */}
          <Sidebar
            title={'SIDEBAR TITLE'}
            onListItemClick={this.onMarkerClick.bind(this)}
            places={this.state.places}/>
          <div className={styles.content}>
            {this.props.children}
          </div>
        </Map>
        <table className="table flex-column">
          <thead className="thead-light">
            <tr>
              <th>Collection Name</th>
              <th>Place Name</th>
              <th>Type</th>
              <th>Address</th>
              <th>Comments</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            { this.collectionsList() }
          </tbody>
        </table>
      </div>
    )
  }
}
