import React from 'react';
import { PropTypes as T } from 'prop-types';
import Map, {GoogleApiWrapper, Marker} from 'google-maps-react';
import styles from './styles.module.css';
import './styles.module.css';
import {searchNearby, searchQuery, geoLocateUser, initAutocomplete} from '../../utils/googleApiHelpers';


export class MapComponent extends React.Component {
  constructor(props, context) {
    super(props, context)
  }

  onReady(mapProps, map) {
    initAutocomplete(this.props.google, map)
  }

  render() {
    const {children} = this.props;

    return (
      <Map map={this.props.map}
        onReady={this.onReady.bind(this)}
        google={this.props.google}
        className={styles.map}
        //zoom={this.props.zoom}
        zoomControl={true}
        zoomControlOptions={{position: this.props.google.maps.ControlPosition.LEFT_CENTER}}
        onRecenter={this.props.onMove}
        onDragend={this.props.onMove}
        onClick={this.props.onClick}
        visible={!children || React.Children.count(children) == 0}
        initialCenter = {{ lat: 40.7812, lng: -73.9665 }}
        >
          <input
            id="pac-input"
            className="controls"
            type="text"
            placeholder="Search Box"
          />
      </Map>
    )
  }
}

MapComponent.propTypes = {
  onMarkerClick: T.func
}
const identity = (...a) => a;
MapComponent.defaultProps = {
  onMarkerClick: identity
}

const GAPI_KEY = process.env.REACT_APP_GAPI_KEY;
export default GoogleApiWrapper({
  apiKey: GAPI_KEY
})(MapComponent)
