import React, { Component } from 'react';
import axios from 'axios';
const mongoose = require('mongoose')

export default class EditCollection extends Component {
  constructor(props) {
    super(props);

    this.onSelectCollection = this.onSelectCollection.bind(this);
    this.onChangeCollectionName = this.onChangeCollectionName.bind(this);
    this.onDeleteCollection = this.onDeleteCollection.bind(this);
    this.onAddPlace = this.onAddPlace.bind(this);
    this.onDeletePlaceFromCollection = this.onDeletePlaceFromCollection.bind(this);
    this.onEditPlace = this.onEditPlace.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      currentCollection: '',
      currentPlaces: [],
      collections: []
    }
  }

  componentDidMount() { // plug in code example on nested table: https://www.freakyjolly.com/react-table-tutorial/#Adding_Pagination_in_Tables_created_by_using_react-table
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        this.setState({ 
          collections: response.data.collectionsList,
          currentCollection: this.state.collections[0]
         })
      })
      .catch((error) => {
        console.log(error);
      })
  }


  onSelectCollection(e) {
    this.setState({
      currentCollection: e.target.value
    })
  }

  onChangeCollectionName(e) { // follow code example here: https://stackoverflow.com/questions/43391592/mongoose-rename-collection
    /*
    let db = mongoose.connection.db;
    newCollectionName = 'filler new connection name';
    return db.collection(currentCollectionName).rename(newCollectionName)
      .then(() => console.log('collection rename succesful!'))
      .catch(e => console.log('collection rename failed: ', e.message))
    */
  }

  onDeleteCollection(e) {
  }

  onAddPlace(e) {

  }

  onEditPlace(e) {

  }

  onDeletePlaceFromCollection(e) { // Read removing SubDocs: https://mongoosejs.com/docs/subdocs.html
    /*
      axios.delete('http://localhost:5000/collections/'+placeName)
        .then(response => { console.log(response.data)});

      this.setState({
        collections: this.state.collections.filter(el => el._placeName !== placeName)
      })
    */
  }

  onSubmit(e) {
    e.preventDefault();

    const collection = {
      createdBy: this.state.username,
      collectionName: this.state.collectionName,
      children: []
      //date: this.state.date
    }

    console.log(collection);

    axios.post('http://localhost:5000/collections/add', collection)
      .then(res => {
        console.log(res.data);
        alert("Collection successfully created!");
        //window.location = '/';
      })
      .catch(err => {
        alert("Error " + err)
      })
  }
  //tbody className = {styles.wrapper} -> if needed
  render() {
    return (
      <div>
        <h3>View Collections</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Collection Name</th>
              <th>Place Name</th>
              <th>Type</th>
              <th>Address</th>
              <th>Comments</th>
            </tr>
          </thead>
          <tbody>
            { this.collectionsList() }
          </tbody>
        </table>
      </div>
    )
  }
}
