import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import MapContainer from '../../views/maps-container'
import styles from './styles.module.css'
/*
const Collection = props => (
  <tr>
    <td>{props.collection.collectionName}</td>
    <td>{props.collection.collecti}</td>
    <td>{props.places.name}</td>
    <td>{props.places.address}</td>
    <td>{props.places.type}</td>
    <td>{props.places.comments}</td>
    <td>
      <Link to={"/edit/"+props.places.__id}>edit</Link> | <a href="#" onClick={e => { props.deletePlaceFromCollection(props.places.name) }}>delete</a>
    </td>
  </tr>
)
*/

export default class CollectionsList extends Component {
  constructor(props) {
    super(props);

    this.deletePlace = this.deletePlaceFromCollection.bind(this);
    this.viewCollection = this.viewCollection.bind(this);

    this.state = {collections: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        this.setState({ collections: response.data.collectionsList.filter(el => el.collectionName !== 'users') })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  viewCollection() {

  }

  deletePlaceFromCollection(e) {
    /*
    axios.delete('http://localhost:5000/collections/'+placeName)
      .then(response => { console.log(response.data)});

    this.setState({
      collections: this.state.collections.filter(el => el._placeName !== placeName)
    })
    */
  }

  collectionsList() {
    return this.state.collections.map(currentCollection => {
      return <Collection 
        collection={currentCollection.name} 
        key={currentCollection._collectionName}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>View Collections</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Collection Name</th>
              <th>Place Name</th>
              <th>Type</th>
              <th>Address</th>
              <th>Comments</th>
            </tr>
          </thead>
          <tbody className = {styles.wrapper}>
            { this.collectionsList() }
          </tbody>
        </table>
        <div>
          <MapContainer />
        </div>
      </div>
    )
  }
}
