import React, { Component } from 'react';
import axios from 'axios';

export default class CreateCollection extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeCollectionName = this.onChangeCollectionName.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      collectionName: '',
      users: [],
      collections: [],
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        if (response.data.usersList.length > 0) {
          this.setState({
              users: response.data.usersList.map(user => user.username),
              username: response.data.usersList[0].username
        })
      }
    })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onChangeCollectionName(e) {
    this.setState({
      collectionName: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const collection = {
      createdBy: this.state.username,
      collectionName: this.state.collectionName,
    }

    axios.post('http://localhost:5000/collections/add', collection)
      .then(res => {
        console.log(res.data);
        alert("Collection successfully created!");
        window.location = '/';
      })
      .catch(err => {
        alert(err)
      })
  }

  render() {
    return (
    <div>
      <div className="component-title">Create New Collection</div>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Username: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}>
              {
                this.state.users.map(function(user) {
                  return <option 
                    key={user}
                    value={user}>{user}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
            <label>Collection Name: </label>
            <input type="text"
                required
                className="form-control"
                value={this.state.collectionName}
                onChange={this.onChangeCollectionName}
                />
          </div>
        <div className="form-group">
          <input type="submit" value="Create Collection" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
