import React, { Component } from 'react';
import axios from 'axios';
import MapsViewContainer from '../../views/Maps-View-Container/maps-view-container';


export default class ViewCollections extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentCollection: '',
      collections: []
    };
  }

  componentDidMount() {
    axios.get(`https://gentle-tundra-70710.herokuapp.com/api/collections`)
      .then(response => {
        this.setState({ 
          collections: response.data.collectionsList.sort((a, b) => (a.name > b.name) ? 1 : -1).filter(el => el.name !== 'users'),
        })
      })
      .catch((error) => {
        console.log(error);
      })
    }

  render() {
    return (
      <div>
        <div className="component-title">View Collections --- Search for a place, click on any marker(s) that pops up to get place info. Then copy place and paste info into "Add Place to Collection" page </div>
        <MapsViewContainer collections={this.state.collections}
                              />
      </div>
    )
  }
}
