import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../src/App.css';
import { BrowserRouter as Router, Route, matchPath} from 'react-router-dom';

import Navbar from "./components/navbar.component"
import ViewCollections from "./components/View-Collections/View-Collections";
import CreateCollection from "./components/create-collection.component";
import CreateUser from "./components/create-user.component";
import Detail from "../src/views/Detail/Detail";

import { createBrowserHistory } from 'history';
// <Route path="/edit/:id" component={EditCollections} /> **** very important for later.. when identifying specific roots/items

const getParams = pathname => {
  const matchPlace = matchPath(pathname, {
    path: `/detail/:placeId`,
    exact: true //maybe?  change this if not working right
  });
  return (matchPlace && matchPlace.params) || {}
}

class App extends React.Component {

  state = {
    loading: false
  };

  render () {
    return (
      <Router className="container">
        <Navbar />
          <Route path="/" exact component={ViewCollections} />
          <Route 
            path="/detail/:placeId" 
            render={props => {
              return <ViewCollections {...props} loading={this.state.loading} />
            }}
            component={Detail} />
          <Route path="/edit" />
          <Route path="/create" component={CreateCollection} />
          <Route path="/user" component={CreateUser} />
      </Router>
    );
  }
}

export default App;
