import React from 'react'
import Map, {GoogleApiWrapper} from 'google-maps-react'
import MapComponent from './Map/Map';

import Sidebar from '../components/Sidebar/Sidebar'
import styles from './styles.module.css'

import { BrowserRouter as Router, Route, matchPath} from 'react-router-dom';

const mapStyles = {
  width: '100%',
  height: '100%'
};

const getParams = pathname => {
  const matchPlace = matchPath(pathname, {
    path: `/detail/:placeId`,
  });
  return (matchPlace && matchPlace.params) || {};
};


export class MapsViewContainer extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.setCurrentPlaces = this.setCurrentPlaces.bind(this);
    this.onClickPlace = this.onClickPlace.bind(this);

    this.state = {
      currentPlacesFromCollection: [],
      pagination: null,
      detailItem: null 
    }
  }

  setCurrentPlaces (currentPlacesFromCollection) {
    this.setState({
      currentPlacesFromCollection: currentPlacesFromCollection
    })
  }

  componentDidUpdate(prevProps, prevState) {
    const { pathname } = this.props.location;
    const { pathname: prevPathname } = prevProps.location;

    const currentParams = getParams(pathname);
    const prevParams = getParams(prevPathname);

    if (this.state.currentPlacesFromCollection !== prevState.currentPlacesFromCollection ) {
      this.setState( { currentPlacesFromCollection:  this.state.currentPlacesFromCollection} )
    }
  }

  onClickPlace(item) { 
    this.setState({ detailItem: item }) //sends specific item to Map component for pushing detail page
    // trigger function in child
  }

  render() {
    let children = null;
    if (this.props.children) {
      console.log("Container check for props to see children: ", this.props)
      children = React.cloneElement(this.props.children, {
        google: this.props.google,
        loaded: this.props.loaded,
        router: this.context.router,
        onMove: this.onMapMove.bind(this),
        onPlaceClick: this.onMarkerClick.bind(this),
        zoom: this.props.zoom
      })
    } else {
        console.log("No children in props in my maps container: ", this.props)
      }


    return (
        <Map
          google={this.props.google}
          visible={false}
          className={styles.wrapper}
          style={mapStyles}
          >
          <Sidebar
            title={'Collections List'}
            collections={this.props.collections}
            className="Sidebar"
            setCurrentPlaces={this.setCurrentPlaces}
            onClickPlace={this.onClickPlace}
          >      
            <div className={styles.content}>
              {this.props.children}
            </div>    
          </Sidebar>
          <MapComponent 
            currentPlacesFromCollection={this.state.currentPlacesFromCollection}
            detailItem={this.state.detailItem}
            history={this.props.history}/>
        </Map>
    )
  }
}


const GAPI_KEY = process.env.REACT_APP_GAPI_KEY;
export default GoogleApiWrapper({
  apiKey: GAPI_KEY
})(MapsViewContainer)
