import React, { Component } from 'react';
import axios from 'axios';

export default class EditCollection extends Component {
  constructor(props) {
    super(props);

    this.onChangeCollectionName = this.onChangeCollectionName.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      collectionName: '',
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        if (response.data.collectionsList.length > 0) {
          this.setState({
              collections: response.data.collectionsList.map(collection => collection.name),
              collection: response.data.collectionsList[0].name
        })
      }
    })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeCollectionName(e) {
    this.setState({
      collectionName: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const request = {
      collectionName: this.state.collectionName,
    }

    axios.post('http://localhost:5000/collections/delete', request)
      .then(res => {
        console.log(res.data);
        alert("Collection successfully deleted!");
        //window.location = '/';
      })
      .catch(err => {
        alert(err)
      })
  }

  render() {
    return (
    <div>
      <div className="component-title">Edit Collections Collection</div>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Username: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}>
              {
                this.state.users.map(function(user) {
                  return <option 
                    key={user}
                    value={user}>{user}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
            <label>Collection Name: </label>
            <input type="text"
                required
                className="form-control"
                value={this.state.collectionName}
                onChange={this.onChangeCollectionName}
                />
          </div>
        <div className="form-group">
          <input type="submit" value="Delete Collection" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
