import React, { Component } from 'react'
import { PropTypes as T } from 'prop-types'
import classnames from 'classnames'

import Item from './Item';
import styles from './styles.module.css'
import Collection from './Collection'
import styled from "styled-components"; 
import { Link } from "react-router-dom"; 

import * as FaIcons from "react-icons/fa"; 
import * as AiIcons from "react-icons/ai"; 
import * as IoIcons from "react-icons/io"; 
import * as RiIcons from "react-icons/ri"; 


const SidebarLink = styled(Link)` 
  display: flex; 
  color: #e1e9fc; 
  justify-content: space-between; 
  align-items: center; 
  padding: 20px; 
  list-style: none; 
  height: 60px; 
  text-decoration: none; 
  font-size: 18px; 

  &:hover { 
    background: #252831; 
    border-left: 4px solid green; 
    cursor: pointer; 
  } 
`; 

const SidebarLabel = styled.span` 
  margin-left: 16px; 
`; 

const DropdownLink = styled(Link)` 
  background: #252831; 
  height: 60px; 
  padding-left: 3rem; 
  display: flex; 
  align-items: center; 
  text-decoration: none; 
  color: #f5f5f5; 
  font-size: 18px; 

  &:hover { 
    background: green; 
    cursor: pointer; 
  } 
`; 

const iconClosed = <RiIcons.RiArrowDownSFill />
const iconOpened = <RiIcons.RiArrowUpSFill />


export class Listing extends Component {
  constructor(props, context) {
    super(props, context);

    this.collectionsList = this.collectionsList.bind(this);
    this.showSubnav.bind(this);

    this.state = {
      subnav: false,
    }
  }

  showSubnav = () => this.setState({subnav: !this.state.subnav})

  collectionsList() {
    return this.props.collections.map(collection => {
      return (
      <Collection 
        collectionName={collection.name}
        placeName={""}
        address={""}
        type={""}
        comments={""}
        key={collection.collectionName}
        onClick={this.showSubnav}>
          {this.state.subnav
          ? iconOpened
          : this.state.subnav
          ? iconClosed
          : null}
      </Collection> 
      )
    })
  }

  render() {
    return (
      <div className={classnames(styles.container)}>
        {this.collectionsList()}
      </div>
      /*
      <div className={classnames(styles.container)}>
      {this.props.places.map(place => {
        return (
          <Item place={place}
                onClick={this.props.onClick}
                onHighlight={this.props.onHighlight}
                offHighlight={this.props.offHighlight}
                key={place.id} />
        )
      })}
      </div>
      */
    )
  }
}

Listing.propTypes = {
  places: T.array.isRequired,
  onHighlight: T.func,
  offHighlight: T.func,
  onClick: T.func
}

Listing.defaultProps = {
  onHighlight: () => {},
  offHighlight: () => {},
  onClick: () => {},
}

export default Listing
