import React from 'react'
import { PropTypes as T } from 'prop-types';
import classnames from 'classnames';

import Listing from '../Listing/Listing';
import styles from './styles.module.css';

// dropdown sidebar stuff
import styled from "styled-components"; 
import { Link } from "react-router-dom"; 
import * as FaIcons from "react-icons/fa"; 
import * as AiIcons from "react-icons/ai"; 
//import { SidebarData } from "./SidebarData"; 
//import SubMenu from "./SubMenu"; 
import { IconContext } from "react-icons/lib"; 


// reference for sidebar https://github.com/briancodex/react-sidebar-dropdown/blob/main/src/components/Sidebar.js


const Nav = styled.div` 
  background: #15171c; 
  height: 80px; 
  display: flex; 
  justify-content: flex-start; 
  align-items: center; 
`; 


const NavIcon = styled(Link)` 
  margin-left: 2rem; 
  font-size: 2rem; 
  height: 80px; 
  display: flex; 
  justify-content: flex-start; 
  align-items: center; 
`; 

const SidebarNav = styled.nav` 
  background: #15171c; 
  width: 300px; 
  height: 100vh; 
  display: flex; 
  justify-content: center; 
  position: fixed; 
  top: 0; 
  left: ${({ sidebar }) => (sidebar ? "0" : "-100%")}; 
  transition: 350ms; 
  z-index: 10; 
`; 

const SidebarWrap = styled.div` 
  width: 100%; 
`; 

export class Sidebar extends React.Component {
  constructor(props) {
    super(props);

    this.showSidebar = this.showSidebar.bind(this);

    this.state = {
        sidebar: false,
        setSidebar: false,
        places: [],
        isCollectionOpen: false,
    };
  }

  showSidebar = () => this.setState({sidebar: !this.state.sidebar})

  onClick(place, map, google) {
    if (this.props.onListItemClick) {
      place.place = place;
      this.props.onListItemClick(place, map, google)
    }
  }

  render() {
    return (
      <div className={classnames(styles.sidebar)}>
        <IconContext.Provider value={{ color: "#fff" }}> 
        <Nav> 
          <NavIcon to="#"> 
            <FaIcons.FaBars onClick={this.showSidebar} /> 
          </NavIcon> 
            <h2>{this.props.title}</h2>
        </Nav> 
        <SidebarNav sidebar={this.state.sidebar}> 
          <SidebarWrap> 
            <NavIcon to="#"> 
              <AiIcons.AiOutlineClose onClick={this.showSidebar} /> 
            </NavIcon> 
            <div className={classnames(styles.listingRatings)}>
              <Listing className="listing" places={this.props.places}
                      collections={this.props.collections}
                      onClick={this.onClick.bind(this)} />
            </div>
          </SidebarWrap> 
        </SidebarNav> 
      </IconContext.Provider>
      </div>
    )
  }
}

Sidebar.propTypes = {
  places: T.array,
  title: T.string,
  onListItemClick: T.func
}

Sidebar.defaultProps = {
  title: 'Places'
}
