import React, { Component } from 'react';
import axios from 'axios';

export default class AddtoCollection extends Component {
  constructor(props) {
    super(props);

    this.onChangeCollection = this.onChangeCollection.bind(this);
    this.onChangePlaceId = this.onChangePlaceId.bind(this);
    this.onChangePlaceName = this.onChangePlaceName.bind(this);
    this.onChangeComments = this.onChangeComments.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      collection: '',
      collections: [],
      placeId: '',
      placeName: '',
      comments: ''
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        if (response.data.collectionsList.length > 0) {
          this.setState({
              collections: response.data.collectionsList.map(collection => collection.name),
              collection: response.data.collectionsList[0].name
        })
      }
    })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeCollection(e) {
    this.setState({
      collection: e.target.value
    })
  }

  onChangePlaceId(e) {
    this.setState({
      placeId: e.target.value
    })
  }

  onChangePlaceName(e) {
    this.setState({
      placeName: e.target.value
    })
  }

  onChangeComments(e) {
    this.setState({
      comments: e.target.value
    })
  }


  onSubmit(e) {
    e.preventDefault();

    const place = {
      collectionName: this.state.collection,
      placeId: this.state.placeId,
      placeName: this.state.placeName,
      comments: this.state.comments
    }

    console.log("place object we're submitting: ", place);

    axios.post('http://localhost:5000/collections/place/add', place)
      .then(res => {
        console.log(res.data);
        alert("Collection successfully created!");
        //window.location = '/';
      })
      .catch(err => {
        alert(err)
      })
  }

  render() {
    return (
    <div>
      <div className="component-title">Add Place to Collection</div>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Collection to Add to: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.collection}
              onChange={this.onChangeCollection}>
              {
                this.state.collections.map(function(collection) {
                  return <option 
                    key={collection}
                    value={collection}>{collection}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
            <label>Place Id</label>
            <input type="text"
                required
                className="form-control"
                value={this.state.placeId}
                onChange={this.onChangePlaceId}
                />
          </div>
        <div className="form-group"> 
          <label>Place Name</label>
          <input type="text"
              required
              className="form-control"
              value={this.state.placeName}
              onChange={this.onChangePlaceName}
              />
        </div>
        <div className="form-group"> 
          <label>Comments</label>
          <input type="text"
              className="form-control"
              value={this.state.comments}
              onChange={this.onChangeComments}
              />
        </div>
        <div className="form-group">
          <input type="submit" value="Create Collection" className="btn btn-primary" />
        </div>
      </form>

    </div>
    )
  }
}
