import React from 'react'
import { PropTypes as T } from 'prop-types'
import Map, {GoogleApiWrapper} from 'google-maps-react'
import {searchNearby, searchQuery, geoLocateUser, initAutocomplete} from '../utils/googleApiHelpers'
import MapComponent from './Map/Map';


//import Header from '../components/Header/Header'
import Sidebar from '../components/Sidebar/Sidebar'
import styles from './styles.module.css'
import styled from "styled-components"; 
//import './maps-edit-container.css'

const SidebarNav = styled.nav` 
  background: #15171c; 
  width: 300px; 
  height: 100vh; 
  display: flex; 
  justify-content: center; 
  position: fixed; 
  top: 0; 
  left: ${({ sidebar }) => (sidebar ? "0" : "-100%")}; 
  transition: 350ms; 
  z-index: 10; 
`; 

const SidebarWrap = styled.div` 
  width: 100%; 
`; 

const mapStyles = {
  width: '100%',
  height: '100%'
};

export class MapsViewContainer extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.showSidebar = this.showSidebar.bind(this);

    this.state = {
      sidebar: true,
      places: [],
      pagination: null
    }
  }

  showSidebar = () => this.setState({sidebar: !this.state.sidebar})

  onReady(mapProps, map) {
    initAutocomplete(this.props.google, map)
    searchNearby(
      this.props.google,
      map,
      {
        location: map.center,
        radius: '1000',
        types: ['bank']
      }
    ).then((results, pagination) => {
      this.setState({
        places: results,
        pagination
      })
    }).catch((status) => {
      console.log('error fetching nearby', status)
    })
  }

  onMapMove() {}

  onMarkerClick(item) { // history stuff
    //const {push} = this.context.router;
    const {place} = item;
    //push(`/map/detail/${place.place_id}`)
  }

  render() {
    let children = null;
    if (this.props.children) {
      children = React.cloneElement(this.props.children, {
        google: this.props.google,
        places: this.state.places,
        loaded: this.props.loaded,
        router: this.context.router,
        onMove: this.onMapMove.bind(this),
        onMarkerClick: this.onMarkerClick.bind(this),
        zoom: this.props.zoom
      })
    }

    return (
        <Map
          google={this.props.google}
          onReady={this.onReady.bind(this)}
          visible={false}
          className={styles.wrapper}
          style={mapStyles}
          initialCenter = {{ lat: 40.7831, lng: -73.9712 }}
          zoomControl={true}
          zoomControlOptions={{position: this.props.google.maps.ControlPosition.LEFT_CENTER}}>
          {/*
          <input
            id="pac-input"
            className="controls"
            type="text"
            placeholder="Search Box"
          />
          */}

          <IconContext.Provider value={{ color: "#fff" }}> 
            <Nav> 
              <NavIcon to="#"> 
                <FaIcons.FaBars onClick={this.showSidebar} /> 
              </NavIcon> 
                <h2>{this.props.title}</h2>
            </Nav> 
          <SidebarNav sidebar={this.state.sidebar}> 
            <SidebarWrap> 
              <NavIcon to="#"> 
                <AiIcons.AiOutlineClose onClick={this.showSidebar} /> 
              </NavIcon> 
                <Sidebar
                  title={'SIDEBAR TITLE'}
                  onListItemClick={this.onMarkerClick.bind(this)}
                  places={this.state.places}
                  collections={this.props.collections}
                >      
                  <div className={styles.content}>
                    {this.props.children}
                  </div>    
              </Sidebar>
            </SidebarWrap> 
          </SidebarNav> 
          </IconContext.Provider>

          <MapComponent/>    
        </Map>
    )
  }
}

MapsViewContainer.contextTypes = {
  router: T.object
}

const GAPI_KEY = process.env.REACT_APP_GAPI_KEY;
export default GoogleApiWrapper({
  apiKey: GAPI_KEY
})(MapsViewContainer)
