import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

import styles from './styles.module.css';
import classnames from 'classnames';

import Item from './Item';


import styled from "styled-components"; 
import * as FaIcons from "react-icons/fa"; 
import * as AiIcons from "react-icons/ai"; 
import * as IoIcons from "react-icons/io"; 
import * as RiIcons from "react-icons/ri"; 


const SidebarLink = styled(Link)` 
  display: flex; 
  color: #e1e9fc; 
  justify-content: space-between; 
  align-items: center; 
  padding: 20px; 
  list-style: none; 
  height: 60px; 
  text-decoration: none; 
  font-size: 18px; 

  &:hover { 
    background: #252831; 
    border-left: 4px solid green; 
    cursor: pointer; 
  } 
`; 

const SidebarLabel = styled.span` 
  margin-left: 16px; 
`; 

const DropdownLink = styled(Link)` 
  background: #252831; 
  height: 60px; 
  padding-left: 3rem; 
  display: flex; 
  align-items: center; 
  text-decoration: none; 
  color: #f5f5f5; 
  font-size: 18px; 

  &:hover { 
    background: green; 
    cursor: pointer; 
  } 
`; 

const iconClosed = <RiIcons.RiArrowDownSFill />
const iconOpened = <RiIcons.RiArrowUpSFill />



export default class Collection extends Component {

    constructor(props) {
      super(props);

      this.showSubnav.bind(this);

      this.state = {
          places: [],
          isCollectionOpen: false,
          subnav: false,
      };
    }

    componentDidMount() {
      const collectionQuery = {
          collectionName: this.props.collectionName
      }

      axios.post('http://localhost:5000/collections/collectionName/', collectionQuery)
        .then(response => {
          this.setState({
              places: response.data
          })
        })
        .catch((error) => {
          console.log(error);
        })
    }

    showSubnav = () => this.setState({subnav: !this.state.subnav})

    toggleCollection = () => {
      this.setState({
        isCollectionOpen: !this.state.isCollectionOpen
      })
    }

    render() {
        const { isCollectionOpen } = this.state.isCollectionOpen;

        return ( 
          <div className = {classnames(styles.item, {[styles.itemHovered]: this.state.hovered})
                /*onClick={this.onClick.bind(this)} */
          }>
            <button type="button" 
              className="dd-header" 
              onClick={this.toggleCollection}>
                <h1 className={classnames(styles.title, "dd-header-title")}> { this.props.collectionName } </h1>
                {isCollectionOpen 
                }
            </button>
            <div className={classnames(styles.container)}> 
              { this.state.places.filter(place => 'placeName' in place).map(place => {
                return ( 
                  <Item place={ place }
                      onClick={this.props.onClick}
                      onHighlight={this.props.onHighlight}
                      offHighlight={this.props.offHighlight}
                      key={place.id}/>
                  )
                })
              }
            </div> 
          </div>
        )
    }
}


