export function searchNearby(google, map, request) {
  var infoWindow = new google.maps.InfoWindow();
  initAutocomplete(google, map, infoWindow)

  return new Promise((resolve, reject) => {
    const service = new google.maps.places.PlacesService(map);
    service.nearbySearch(request, (results, status, pagination) => {
      if (status === google.maps.places.PlacesServiceStatus.OK) {
        for (var i = 0; i < 10; i++) {
          createMarker(google, map, results[i]);
        }
        resolve(results, pagination);
      } else {
        reject(results, status);
      }
    })
  });
}




export function initAutocomplete(google, map, state) {
  const input = document.getElementById("pac-input")
  const searchBox = new google.maps.places.SearchBox(input);
  map.controls[google.maps.ControlPosition.TOP_CENTER].push(input);

  // Bias the SearchBox results towards current map's viewport.
  map.addListener("bounds_changed", () => {
    searchBox.setBounds(map.getBounds());
    google.maps.event.trigger(map, 'resize')
  });
  let markers = [];
  // Listen for the event fired when the user selects a prediction and retrieve
  // more details for that place.
  searchBox.addListener("places_changed", () => {
    const places = searchBox.getPlaces();

    if (places.length === 0) {
      return;
    }
    // Clear out the old markers.
    markers.forEach((marker) => {
      marker.setMap(null);
    });
    markers = [];
    // For each place, get the icon, name and location.
    const bounds = new google.maps.LatLngBounds();

    for (let i = 0; i < places.length; i++) {
      var place = places[i];
      console.log(place)
        if (!place.geometry || !place.geometry.location) {
          console.log("Returned place contains no geometry");
          return;
        }
        // Create a marker for each place.
        createMarker(google, map, place);

        if (place.geometry.viewport) {
          // Only geocodes have viewport.
          bounds.union(place.geometry.viewport);
        } else {
          bounds.extend(place.geometry.location);
        }
    }
    map.fitBounds(bounds);
  });
}

export function createMarkers(google, map, places) {

  if (!places || places.length === 0) {
    console.log("no places: return")
    return
  } else {
    console.log("there are places! ", places)
    //create empty LatLngBounds object   
    for (var i = 0; i < places.length; i++) {
      createMarker(google, map, places[i])
    }

  }
}

export function createMarker(google, map, place) {
  var position;

  if (!place.geometry) {
    if (!place.location) {
      return
    }
    position = place.location;
  } else {
    position = place.geometry.location;
    console.log(place)
  }

  //if (!place.geometry || !place.geometry.location) return;

  var marker = new google.maps.Marker({
    map,
    position: position//position, //place.geometry.location
  });

  var bounds = new google.maps.LatLngBounds();
  var infowindow = new google.maps.InfoWindow(); 

  bounds.extend(marker.position);
  google.maps.event.addListener(marker, 'click', (function(marker) {
    return function() {
      infowindow.setContent(place[0]);
      infowindow.open(map, marker);
    }
  })(marker));

  //now fit the map to the newly inclusive bounds
  map.fitBounds(bounds);

  //(optional) restore the zoom level after the map is done scaling
  /*
  var listener = google.maps.event.addListener(map, "idle", function () {
      map.setZoom(3);
      google.maps.event.removeListener(listener);
  });
  */


  google.maps.event.addListener(marker, "click", (function() {
    return function() {
      marker.infoWindow = new google.maps.InfoWindow();
      //var mainType = place.types[0];
      var name = place.name;
      var address = place.formatted_address;
      //var phoneNumber = place.formatted_phone_number;
      var html = "<b>" + name + "</b> <br/> <b> address: " + address + "</b><br/>";
      map.panTo(marker.getPosition());
      marker.infoWindow.setContent(html);
      marker.infoWindow.open(map, marker);
    }(marker);
  }))

  return {
    position: position
  };
}




export function geoLocateUser(google, map) {
  var infoWindow = new google.maps.InfoWindow();

  const locationButton = document.createElement("button");
  locationButton.textContent = "Pan to Current Location"; 
  locationButton.classList.add("custom-map-control-button");
  map.controls[google.maps.ControlPosition.TOP_CENTER].push(locationButton);

  locationButton.addEventListener("click", () => {
    // Try HTML5 geolocation.
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const pos = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          };
          infoWindow.setPosition(pos);
          infoWindow.setContent("Your location");
          //infoWindow.open(map);
          map.setCenter(pos);
        },
        () => {
          handleLocationError(true, infoWindow, map.getCenter());
        }
      );
    } else {
      // Browser doesn't support Geolocation
      handleLocationError(false, infoWindow, map.getCenter());
    }
  });

  function handleLocationError(browserHasGeolocation, infoWindow, pos) {
    infoWindow.setPosition(pos);
    infoWindow.setContent(
      browserHasGeolocation
        ? "Error: The Geolocation service failed."
        : "Error: Your browser doesn't support geolocation."
    );
    infoWindow.open(map);
  }
}


/*
 * getDetails
 * Get details from the Google API about one specific place.
 */
export function getDetails(google, map, placeId) {
  return new Promise((resolve, reject) => {
    const service = new google.maps.places.PlacesService(map);

    const request = {
      placeId
    }

    service.getDetails(request, (place, status) => {
      if (status !== google.maps.places.PlacesServiceStatus.OK) {
        return reject(status);
      } else {
        resolve(place);
      }
    })
  })
}
