import React from 'react';
import Map, {GoogleApiWrapper} from 'google-maps-react';
import styles from './styles.module.css';
import './styles.module.css';
import {searchNearby, searchQuery, geoLocateUser, initAutocomplete, createMarkers, createMarker} from '../../utils/googleApiHelpers';


export class MapComponent extends React.Component {
  constructor(props) {
    super(props);


    this.createMarkers = this.createMarkers.bind(this);
  }

  onReady(mapProps, map) {
    initAutocomplete(this.props.google, map)
    geoLocateUser(this.props.google, map)

    createMarkers(this.props.google, map, this.props.currentPlacesFromCollection)
  }

  createMarkers(google, map, places) {
    var infoWindow = new google.maps.InfoWindow();

    const updatePlacesButton = document.createElement("button");
    locationButton.textContent = "Press to Update Places based on State"; 
    locationButton.classList.add("custom-map-control-button");
    map.controls[google.maps.ControlPosition.BOTTOM_RIGHT].push(locationButton);

    locationButton.addEventListener("click", (places) => {
      console.log(places)
    });

    if (!places) {
      return
    } else {
      for (var i = 0; i < places.length; i++) {
        createMarker(google, map, places[i])
      }
    }
  }




  render() {
    const {children} = this.props;

    return (
      <Map 
        onReady={this.onReady.bind(this)}
        google={this.props.google}
        className={styles.map}
        zoomControl={true}
        zoomControlOptions={{position: this.props.google.maps.ControlPosition.LEFT_CENTER}}
        onRecenter={this.props.onMove}
        onDragend={this.props.onMove}
        onClick={this.props.onClick}
        visible={!children || React.Children.count(children) === 0}
        initialCenter = {{ lat: 40.7812, lng: -73.9665 }}
        >
          <input
            id="pac-input"
            className="controls"
            type="text"
            placeholder="Search Box"
          />
          {this.createMarkers(this.props.google, map, this.props.currentPlacesFromCollection)}
      </Map>
    )
  }
}

const GAPI_KEY = process.env.REACT_APP_GAPI_KEY;
export default GoogleApiWrapper({
  apiKey: GAPI_KEY
})(MapComponent)
