import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import MapsViewContainer from '../../views/maps-view-container';

import Collection from '../Listing/Collection';


export default class CollectionsList extends Component {
  constructor(props) {
    super(props);

    this.deletePlace = this.deletePlaceFromCollection.bind(this);
    this.viewCollection = this.viewCollection.bind(this);

    this.state = {
      currentCollection: '',
      collections: []
    };
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        this.setState({ 
          collections: response.data.collectionsList.filter(el => el.collectionName !== 'users'),
          currentCollection: response.data.collectionsList[0]
        })
      })
      .catch((error) => {
        console.log(error);
      })
    }

