import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import MapContainer from '../views/maps-container'

const Collection = props => (
  <tr>
    <td>{props.collection.name}</td>
    <td>{props.collection.address}</td>
    <td>{props.collection.type}</td>
    <td>{props.collection.comments}</td>
    <td>
      <Link to={"/edit/"+props.collection._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.collection._id) }}>delete</a>
    </td>
  </tr>
)

export default class CollectionsList extends Component {
  constructor(props) {
    super(props);

    this.deleteBusiness = this.deleteBusiness.bind(this)

    this.state = {collections: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/collections/')
      .then(response => {
        this.setState({ collections: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteExercise(id) {
    axios.delete('http://localhost:5000/collections/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      collections: this.state.collections.filter(el => el._id !== id)
    })
  }

  collectionsList() {
    return this.state.collections.map(currentBusiness => {
      return <Collection collection={currentBusiness} deleteExercise={this.deleteBusiness} key={currentBusiness._id}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>View Collections</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Name</th>
              <th>Type</th>
              <th>Address</th>
              <th>Comments</th>
            </tr>
          </thead>
          <tbody>
            { this.collectionsList() }
          </tbody>
        </table>
        <div>
          <MapContainer />
        </div>
      </div>
    )
  }
}
