import React, { useState } from 'react';
import { PropTypes as T } from 'prop-types';
import classnames from 'classnames';

import Listing from '../Listing/Listing';
import styles from './styles.module.css';

// dropdown sidebar stuff
import { Link } from 'react-router-dom'
import styled from 'styled-components';
import * as FaIcons from 'react-icons/fa';
import * as AiIcons from 'react-icons/ai';


// reference for sidebar https://github.com/briancodex/react-sidebar-dropdown/blob/main/src/components/Sidebar.js

const [sidebar, setSidebar] = useState(true);
const showSidebar = () => setSidebar(!sidebar);

const NavIcon = styled(Link)`
  margin-left: 2rem;
  font-size: 2rem;
  height: 80px;
  display: flex;
  justify-content: flex-start;
  align-items: center;
`;

export class Sidebar extends React.Component {

  onClick(place, map, google) {
    if (this.props.onListItemClick) {
      place.place = place;
      this.props.onListItemClick(place, map, google)
    }
  }

  render() {
    return (
      <div className={classnames(styles.sidebar)} sidebar={sidebar}>
        <div className={classnames(styles.heading)}>
          <h2>{this.props.title}</h2>
        </div>
        <div className={classnames(styles.listingRatings)}>
          <Listing className="listing" places={this.props.places}
                  onClick={this.onClick.bind(this)} />
        </div>


      </div>
    )
  }
}

Sidebar.propTypes = {
  places: T.array,
  title: T.string,
  onListItemClick: T.func
}

Sidebar.defaultProps = {
  title: 'Places'
}

export default Sidebar
