import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import styles from './styles.module.css';
import classnames from 'classnames';

import Item from './Item';



export default class Collection extends Component {

  constructor(props) {
    super(props);

    this.state = {
      places: [],
    };

  }

  componentDidMount() {

    const collectionQuery = {
      collectionName: this.props.collectionName
    }

    axios.post('http://localhost:5000/collections/collectionName/', collectionQuery)
      .then(response => {
        this.setState({ 
          places: response.data
        })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  render() {
    return (
      <div
        /*onClick={this.onClick.bind(this)} */
        className={classnames(styles.item, {
        [styles.itemHovered]: this.state.hovered
        })}>
          <h1 className={classnames(styles.title)}>{this.props.collectionName}</h1>


        <div className={classnames(styles.container)}>
          {this.props.places.map(place => {
            return (
              <Item place={place}
                    onClick={this.props.onClick}
                    onHighlight={this.props.onHighlight}
                    offHighlight={this.props.offHighlight}
                    key={place.id} />
            )
          })}
        </div>
      </div>
    )
  }
}


