import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

import styles from './styles.module.css';
import classnames from 'classnames';
import FontAwesome from 'react-fontawesome';
import './font-awesome.css';

import Item from './Item';



export default class Collection extends Component {

    constructor(props) {
      super(props);

      this.state = {
          places: [],
          isCollectionOpen: false,
      };
    }

    componentDidMount() {
      const collectionQuery = {
          collectionName: this.props.collectionName
      }

      axios.post('http://localhost:5000/collections/collectionName/', collectionQuery)
        .then(response => {
          this.setState({
              places: response.data
          })
        })
        .catch((error) => {
          console.log(error);
        })
    }

    toggleCollection = () => {
      this.setState({
        isCollectionOpen: !this.state.isCollectionOpen
      })
    }

    render() {
        const { isCollectionOpen } = this.state.isCollectionOpen;

        return ( 
          <div className = {classnames(styles.item, {[styles.itemHovered]: this.state.hovered})
                /*onClick={this.onClick.bind(this)} */
          }>
            <button type="button" 
              className="dd-header" 
              onClick={this.toggleCollection}>
                <h1 className={classnames(styles.title, "dd-header-title")}> { this.props.collectionName } </h1>
                {isCollectionOpen 
                  ? <i className="fas fa-angle-up" size="2x" /> 
                  : <i className="fas fa-angle-down" size="2x" />
                }
            </button>
            <div className={classnames(styles.container)}> 
              { this.state.places.filter(place => 'placeName' in place).map(place => {
                return ( 
                  <Item place={ place }
                      onClick={this.props.onClick}
                      onHighlight={this.props.onHighlight}
                      offHighlight={this.props.offHighlight}
                      key={place.id}/>
                  )
                })
              }
            </div> 
          </div>
        )
    }
}


